#!/bin/bash

# Linka Cluster Management Script
# This script helps manage the multi-region, multi-service deployment

set -e

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Configuration
COMPOSE_FILE="docker-compose.clusters.yml"
PROJECT_NAME="linka-cluster"

# Logging function
log() {
    echo -e "${GREEN}[$(date +'%Y-%m-%d %H:%M:%S')] $1${NC}"
}

warn() {
    echo -e "${YELLOW}[$(date +'%Y-%m-%d %H:%M:%S')] WARNING: $1${NC}"
}

error() {
    echo -e "${RED}[$(date +'%Y-%m-%d %H:%M:%S')] ERROR: $1${NC}"
}

info() {
    echo -e "${BLUE}[$(date +'%Y-%m-%d %H:%M:%S')] INFO: $1${NC}"
}

# Check if Docker and Docker Compose are installed
check_dependencies() {
    log "Checking dependencies..."
    
    if ! command -v docker &> /dev/null; then
        error "Docker is not installed. Please install Docker first."
        exit 1
    fi
    
    if ! command -v docker-compose &> /dev/null; then
        error "Docker Compose is not installed. Please install Docker Compose first."
        exit 1
    fi
    
    log "Dependencies check passed"
}

# Build all services
build_services() {
    log "Building all services..."
    docker-compose -f $COMPOSE_FILE -p $PROJECT_NAME build --no-cache
    log "Build completed successfully"
}

# Start the cluster
start_cluster() {
    log "Starting Linka cluster..."
    docker-compose -f $COMPOSE_FILE -p $PROJECT_NAME up -d
    
    # Wait for services to be healthy
    log "Waiting for services to be ready..."
    sleep 30
    
    # Check service health
    check_health
    
    log "Cluster started successfully"
    show_endpoints
}

# Stop the cluster
stop_cluster() {
    log "Stopping Linka cluster..."
    docker-compose -f $COMPOSE_FILE -p $PROJECT_NAME down
    log "Cluster stopped successfully"
}

# Restart the cluster
restart_cluster() {
    log "Restarting Linka cluster..."
    stop_cluster
    start_cluster
}

# Show cluster status
show_status() {
    log "Cluster Status:"
    docker-compose -f $COMPOSE_FILE -p $PROJECT_NAME ps
}

# Show logs
show_logs() {
    local service=$1
    if [ -z "$service" ]; then
        log "Showing logs for all services..."
        docker-compose -f $COMPOSE_FILE -p $PROJECT_NAME logs -f
    else
        log "Showing logs for service: $service"
        docker-compose -f $COMPOSE_FILE -p $PROJECT_NAME logs -f $service
    fi
}

# Check health of services
check_health() {
    log "Checking service health..."
    
    local services=(
        "http://localhost:8080/health"
        "http://localhost:3001/api/health"
        "http://localhost:3002/api/health"
        "http://localhost:3003/api/health"
        "http://localhost:3004/api/health"
    )
    
    for url in "${services[@]}"; do
        if curl -f -s "$url" > /dev/null; then
            log "✓ $url is healthy"
        else
            warn "✗ $url is not responding"
        fi
    done
}

# Scale a specific service
scale_service() {
    local service=$1
    local replicas=$2
    
    if [ -z "$service" ] || [ -z "$replicas" ]; then
        error "Usage: scale <service> <replicas>"
        exit 1
    fi
    
    log "Scaling $service to $replicas replicas..."
    docker-compose -f $COMPOSE_FILE -p $PROJECT_NAME up -d --scale $service=$replicas
    log "Scaling completed"
}

# Show service endpoints
show_endpoints() {
